
public class Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Simple Create a Arrary
		int[] num = {4,5,6,8};
		System.out.println(num[3]);
		//Access the Element of Array
		String[] cars= {"nexon","baleno","kia"};
		System.out.println(cars[0]);
		//Change in Array element
		String[] carss= {"Nexon","Baleno","KIA"};
		carss[2]="Swift";
        System.out.println(carss[2]);
        //Array Lenght
        System.out.println(carss.length);
	}

}
